
public class For {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=0;i<5;System.out.println("hi")) { 
			
			System.out.println("Hello");
			i++;
		
		}
		
		for(int i=333;i<=444;i+=5)
		{
			System.out.println("Hello" + " " + i);
		}
	}

}
